mapper.select:
SELECT id, 0 AS parent_id, name, sort, created_at, updated_at
FROM category1
WHERE id = #{id}

SELECT id, parent_id, name, sort, created_at, updated_at
FROM category2
WHERE id = #{id}

mapper.update:
UPDATE category1
SET name = #{name},
    sort = #{sort}
WHERE id = #{id}

UPDATE category2
SET parent_id = #{parentId},
    name = #{name},
    sort = #{sort}
WHERE id = #{id}
