mapper.select:
SELECT
FROM goods g LEFT JOIN category2 c2 ON g.category2_id = c2.id WHERE g.is_deleted = 0
AND g.category2_id = #{category2Id}
AND c2.parent_id = #{category1Id}
ORDER BY g.created_at DESC

mapper.insert:
INSERT INTO goods (
    category2_id, category_id, name, price, stock, bar_code, image_url,
    description, is_on_sale, expire_date
) VALUES (
    #{category2Id}, #{category2Id}, #{name}, #{price}, #{stock}, #{barCode}, #{imageUrl},
    #{description}, #{isOnSale}, #{expireDate}
)
