mapper.insert:
INSERT INTO orders (
    order_no, user_id, total_amount, status, verify_code, remark
) VALUES (
    #{orderNo}, #{userId}, #{totalAmount}, #{status}, #{verifyCode}, #{remark}
)

mapper.select:
SELECT * FROM orders


        AND status = #{status}


ORDER BY created_at DESC
LIMIT #{limit} OFFSET #{offset}
